package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1-100
		//FOR,Ctrl+Space, click option 2
		//1=0  --Initial Value
		//i<10; i<num  --cond
		//i++  --increment i+1
		
		int num=10;
		for (int i = 5; i < num; i++) {
			System.out.println(i);
		}
		System.out.println("DECREMENT");
		for (int i = 10; i > 5; i--) {
			System.out.println(i);
		}
	}

}
