package week1.day2;

// Class Name : Starts with UC following with UC (Pascal case)
public class LearnPrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//example  bicycle
		//method starts : LC can be followed by UC (Camelcase)
		int noOfWheel=2;
		char logo='h';
		boolean bell=true; //primi & so starts with lc 'int'
		String brand="Street Fire"; // non-primitive & so starts with caps'String'
	
	System.out.println(noOfWheel);
	System.out.println(logo);
	System.out.println(bell);
	System.out.println(brand);
	
	}

}
